$(function(){
    $('.form-horizontal').ajaxForm({
        success: function(res) {
            if(res.errno == 0){
                $.apolloTip({
                    refresh: true,
                    tip: res.message,
                    closeTime: 1500
                });
                return window.location.href = "/cttask/host/index";
            }else{
                return $.apolloTip({
                    refresh: true,
                    tip: res.message,
                    closeTime: 1500
                });

            }
        }
    }, 'json');
});